  //       return (
  //         <option value={feeds[item].code} key={index}>
  //           {feeds[item].symbol}
  //         </option>
  //       );
  //     });
  //   }
  // };

  return (
    <div>
      Feeds
      {renderPosts()}
      {/* <ul>{listItems}</ul> */}
    </div>
  );
};

export default Feeds;
