import React, { Component, useEffect } from "react";
// import MuiAlert from "@material-ui/lab/Alert";
import { useDispatch, useSelector } from "react-redux";
import { fetchUser, userSelector } from "../../slices/auth";
import {
  TextField,
  Grid,
  Card,
  CardContent,
  Typography,
  Button,
} from "@material-ui/core";

const Login = () => {
  const dispatch = useDispatch();
  const { user, loading, hasErrors } = useSelector(userSelector);

  userEffect(() => {
    dispatch(fetchUser());
  }, [dispatch]);

  // const renderPosts = () => {
  //   if (loading) return <p>Loading posts...</p>;
  //   if (hasErrors) return <p>Unable to display posts.</p>;

  //   return user.map((user) => <User key={user.id} post={user} excerpt />);
  // };

  return (
    <div>
      <Grid className="mt-2" container justify="center" alignItems="center">
        <Grid item xs={6}>
          <Card className="mt-2">
            <CardContent>
              <Typography variant="h4" align="center">
                Login
              </Typography>

              <TextField
                id="outlined-full-width"
                label="Email"
                style={{ marginTop: 5, padding: 12 }}
                placeholder="Email Address"
                fullWidth
                margin="normal"
                InputLabelProps={{
                  shrink: true,
                }}
                variant="outlined"
                value={this.state.LoginData.email}
                onChange={(e) => {
                  let { LoginData } = this.state;
                  LoginData.email = e.target.value;
                  this.setState({ LoginData });
                }}
              />

              <TextField
                id="outlined-full-width"
                label="Password"
                type="password"
                style={{ marginTop: 5, padding: 12 }}
                placeholder="Password"
                fullWidth
                margin="normal"
                InputLabelProps={{
                  shrink: true,
                }}
                variant="outlined"
                value={this.state.LoginData.password}
                onChange={(e) => {
                  let { LoginData } = this.state;
                  LoginData.password = e.target.value;
                  this.setState({ LoginData });
                }}
              />

              <Button
                variant="contained"
                color="primary"
                align="center"
                onClick={this.login.bind(this)}
              >
                <Link to="/login" className="button">
                  View Posts
                </Link>
              </Button>

              {/* {error && (
                  <Alert severity="error" onClick={() => setError(null)}>
                    {props.error || error}
                  </Alert>
                )} */}
            </CardContent>
          </Card>
        </Grid>
      </Grid>
      {/* {renderPosts()} */}
    </div>
  );
};

export default Login;
