export function fetchArticles(token) {
  var bearer_token;
  if (token.length > 0) {
    bearer_token = token;
  } else {
    bearer_token = initialState.user.user.token;
  }
  return async (dispatch) => {
    try {
      // const bearer_token = initialState.user.user.token;
      const response = await fetch("http://localhost:5000/api/v1/articles", {
        headers: {
          authorization: "Bearer " + bearer_token,
        },
      });

      const data = await response.json();
      console.log(data.data.message);
      dispatch(getArticlesSuccess(data.data));
    } catch (error) {
      console.log(error);
      dispatch(getArticlesFailure());
    }
  };
}
