import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import { Container, Grid, Button } from "@material-ui/core";
import Avatar from "@material-ui/core/Avatar";
import Divider from "@material-ui/core/Divider";
import FavoriteIcon from "@material-ui/icons/Favorite";
import ShareIcon from "@material-ui/icons/Share";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import IconButton from "@material-ui/core/IconButton";
import Collapse from "@material-ui/core/Collapse";
import { red } from "@material-ui/core/colors";
import MoreVertIcon from "@material-ui/icons/MoreVert";
import Comment from "../../components/Comment";
import clsx from "clsx";
import Moment from "react-moment";
import CardMedia from "@material-ui/core/CardMedia";
import { useSelector, useDispatch } from "react-redux";
import { articleSelector, fetchArticle } from "../../slices/article";
import cover from "../../static/img/article.jpg";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";
import { Field, Form, Formik } from "formik";
import { TextField } from "material-ui-formik-components/TextField";
import { Select } from "material-ui-formik-components/Select";
import Modal from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";
import CircularProgress from "@material-ui/core/CircularProgress";

const useStyles = makeStyles((theme) => ({
  media: {
    height: 250,
  },
  root: {
    width: "100%",
    maxWidth: 360,
    backgroundColor: "#fff",
  },
  bs: {
    boxShadow: "2px 2px 7px 1px #ccc",
  },
  media2: {
    height: 0,
    paddingTop: "56.25%", // 16:9
  },
  list: {
    maxHeight: 300,
    overflow: "scroll",
  },
  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: "rotate(180deg)",
  },
  avatar: {
    backgroundColor: red[500],
  },
  inline: {
    fontSize: 16,
  },
}));
function ListItemLink(props) {
  return <ListItem button component="a" {...props} />;
}

const ArticleDetail = ({ match }) => {
  const classes = useStyles();

  const [expanded, setExpanded] = React.useState(false);
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  const dispatch = useDispatch();
  const { article, loading, hasErrors } = useSelector(articleSelector);

  useEffect(() => {
    const { id } = match.params;
    dispatch(fetchArticle(id));
  });

  return (
    <>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className={classes.paper}>
            <Card>
              <CardContent>
                {/* {renderPosts()} */}
                <Typography variant="h4" align="center">
                  Create Article
                </Typography>
                <Formik
                  initialValues={{
                    form_title: article.title,
                    form_desc: article.description,
                    form_cat: article.category,
                    form_share: article.share,
                  }}
                  onSubmit={(values, { setSubmitting, resetForm }) => {
                    dispatch(postArticle(token, values));
                    // When button submits form and form is in the process of submitting, submit button is disabled
                    setSubmitting(true);

                    // Resets form after submission is complete
                    resetForm();

                    // Sets setSubmitting to false after form is reset
                    setSubmitting(false);

                    dispatch(fetchArticles(token));

                    handleClose();
                  }}
                >
                  {({ isSubmitting }) => (
                    <Form>
                      <Field
                        id="outlined-full-width"
                        required
                        component={TextField}
                        type="text"
                        name="form_title"
                        label="Title"
                      />
                      <Field
                        id="outlined-full-width"
                        required
                        multiline
                        component={TextField}
                        type="text"
                        name="form_desc"
                        label="Description"
                      />
                      <Field
                        id="outlined-full-width"
                        required
                        component={TextField}
                        type="text"
                        name="form_cat"
                        label="Category"
                      />
                      <Field
                        id="outlined-full-width"
                        required
                        name="form_share"
                        label="Make Article Sharable?"
                        options={[
                          { value: "Yes", label: "Yes" },
                          { value: "No", label: "No" },
                        ]}
                        component={Select}
                      />
                      <Button
                        id="outlined-full-width"
                        type="submit"
                        disabled={isSubmitting}
                        variant="contained"
                        color="primary"
                        align="center"
                        style={{ marginTop: 20, marginLeft: "40%" }}
                      >
                        Submit
                      </Button>
                    </Form>
                  )}
                </Formik>
              </CardContent>
            </Card>
          </div>
        </Fade>
      </Modal>

      <Container maxWidth="md">
        <Grid container spacing={1} style={{ marginTop: 100 }}>
          <Grid item xs={12} md={9}>
            <Card style={{ marginBottom: 20, padding: "0px 15px" }}>
              <CardHeader
                action={
                  <IconButton aria-label="settings">
                    <MoreVertIcon />
                  </IconButton>
                }
                title={article.title}
                subheader={
                  <React.Fragment>
                    <Moment format="LLL">{article.createdat}</Moment>
                  </React.Fragment>
                }
              />
              <CardMedia
                className={classes.media}
                image={cover}
                title="Contemplative Reptile"
              />
              <CardContent>
                <Typography variant="body2" color="textSecondary" component="p">
                  {article.article}
                </Typography>
              </CardContent>
              <CardActions disableSpacing>
                <IconButton aria-label="add to favorites">
                  <FavoriteIcon />
                </IconButton>
                <IconButton aria-label="share">
                  <ShareIcon />
                </IconButton>
                <IconButton
                  className={clsx(classes.expand, {
                    [classes.expandOpen]: expanded,
                  })}
                  onClick={handleExpandClick}
                  aria-expanded={expanded}
                  aria-label="show more"
                >
                  <ExpandMoreIcon />
                </IconButton>
              </CardActions>
              <Collapse in={expanded} timeout="auto" unmountOnExit>
                <Divider />
                <div className="comment">
                  <Grid item>
                    <Avatar
                      alt="Cindy Baker"
                      src="/static/images/avatar/3.jpg"
                    />
                  </Grid>
                  <Grid item xs={8}>
                    <TextField
                      id="outlined-textarea"
                      placeholder="Placeholder"
                      fullWidth
                      multiline
                      variant="outlined"
                    />
                  </Grid>
                  <Grid item>
                    <Button
                      variant="contained"
                      color="primary"
                      style={{ borderRadius: 50 }}
                    >
                      Send
                    </Button>
                  </Grid>
                </div>
                <CardContent>
                  <Comment />
                </CardContent>
              </Collapse>
            </Card>
          </Grid>
          <Grid item xs={12} md={3}>
            <div className={(classes.root, classes.bs)}>
              <List component="nav" aria-label="main mailbox folders">
                <ListItem button onClick={handleOpen}>
                  <ListItemIcon>
                    <EditIcon />
                  </ListItemIcon>
                  <ListItemText primary="Edit" />
                </ListItem>
                <ListItem button>
                  <ListItemIcon>
                    <DeleteIcon />
                  </ListItemIcon>
                  <ListItemText primary="Delete" />
                </ListItem>
              </List>
            </div>
          </Grid>
        </Grid>
      </Container>
    </>
  );
};
export default ArticleDetail;
