import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { feedsSelector, fetchFeeds } from "../../slices/feeds";
import { Container } from "@material-ui/core";
// import { Post } from "../../components/Post";
import Feed from "../../components/Feed";
import Sidebar from "../../components/Sidebar";

const Feeds = (props) => {
  const dispatch = useDispatch();
  const { feeds } = useSelector(feedsSelector);

  const token = props.location.state.id;

  useEffect(() => {
    dispatch(fetchFeeds(token));
  }, [dispatch]);

  const renderFeeds = () => {
    return feeds.map((feed) => <Feed key={feed.id} feed={feed} excerpt />);
  };

  return (
    <div>
      <Container maxWidth="md">
        {renderFeeds()}
        <Sidebar />
      </Container>
    </div>
  );
};

export default Feeds;
