import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";
const initialUser = localStorage.getItem("user")
  ? JSON.parse(localStorage.getItem("user"))
  : null;

export const initialState = {
  user: initialUser,
  feeds: "",
};

// A slice of user with our reducers
const slice = createSlice({
  name: "feeds",
  initialState,
  reducers: {
    getFeeds: (state, action) => {
      state.user = action.payload;
    },
    getFeedsFailure: (state, action) => {
      state.error = action.payload;
    },
  },
});

// Three actions generated from the slice
export const { getFeeds } = slice.actions;

// A selector
export const feedsSelector = (state) => state.feeds;

// The reducer
export default slice.reducer;

// Asynchronous thunk action
export const fetchFeeds = () => async (dispatch) => {
  axios
    .post("http://localhost:5000/api/v1/feed", {
      headers: {
        Authorization: user.token,
      },
    })
    .then((response) => {
      const feed = response.data.data;
      dispatch(getFeeds({ feed }));
    })
    .catch((error) => {
      error = error.response.data.error;
      dispatch(getFeedsFailure({ error }));
    });
};
