import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { useSelector, useDispatch } from "react-redux";
import { feedsSelector, fetchFeeds } from "../../slices/feeds";
import { Container, Grid } from "@material-ui/core";
// import { Post } from "../../components/Post";
import Feed from "../../components/Feed";
import Sidebar from "../../components/Sidebar";

const useStyles = makeStyles((theme) => ({
  media: {
    height: 140,
  },
  root: {
    width: "100%",
    maxWidth: 360,
    backgroundColor: "#fff",
  },
  bs: {
    boxShadow: "2px 2px 7px 1px #ccc",
  },
}));

const Feeds = (props) => {
  const classes = useStyles();
  const dispatch = useDispatch();
  const { feeds } = useSelector(feedsSelector);

  const token = "";
  if (props) {
    // const token = props.location.state.id;
    const token = "f";
  } else {
    const token = "g";
  }

  useEffect(() => {
    dispatch(fetchFeeds(token));
  }, [dispatch]);

  const renderFeeds = () => {
    return feeds.map((feed) => <Feed key={feed.id} feed={feed} excerpt />);
  };

  return (
    <div>
      <Container maxWidth="md">
        <Grid container spacing={1} style={{ marginTop: 100 }}>
          <Grid item xs={12} md={8}>
            {renderFeeds()}
          </Grid>
          <Grid item xs={12} md={4}>
            <div className={(classes.root, classes.bs)}>
              <Sidebar />
            </div>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
};

export default Feeds;
