import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";
const initialUser = localStorage.getItem("user")
  ? JSON.parse(localStorage.getItem("user"))
  : null;
export const initialState = {
  user: initialUser,
  loading: "",
  hasErrors: "",
};

// A slice of user with our reducers
const slice = createSlice({
  name: "user",
  initialState,
  reducers: {
    loginSuccess: (state, action) => {
      state.hasErrors = false;
      state.user = action.payload;
      localStorage.setItem("user", JSON.stringify(action.payload));
    },
    loginFailure: (state, action) => {
      state.hasErrors = true;
    },
    registerSuccess: (state, action) => {
      state.hasErrors = false;
      state.user = action.payload;
      localStorage.setItem("user", JSON.stringify(action.payload));
    },
    registerFailure: (state, action) => {
      state.hasErrors = true;
    },
    logoutSuccess: (state, action) => {
      state.hasErrors = false;
      state.user = null;
      localStorage.removeItem("user");
    },
  },
});

// Three actions generated from the slice
export const {
  loginSuccess,
  loginFailure,
  registerSuccess,
  registerFailure,
  logoutSuccess,
} = slice.actions;

// A selector
export const userSelector = (state) => state.user;

// The reducer
export default slice.reducer;

// Asynchronous thunk action
export const login = ({ username, password }) => async (dispatch) => {
  axios
    .post("http://localhost:5000/api/v1/auth/signin", {
      email: username,
      password: password,
    })
    .then(
      (response) => {
        const { firstname, email, id, token } = response.data.data;
        const user = { firstname, email, id, token };
        dispatch(loginSuccess({ user }));
        console.log(user);
      },
      (error) => {
        dispatch(loginFailure());
        console.error(error);
      }
    );
};

export const register = ({ username, password }) => async (dispatch) => {
  axios
    .post("http://localhost:5000/api/v1/auth/create_user", {
      email: username,
      password: password,
    })
    .then(
      (response) => {
        const { firstname, email, id, token } = response.data.data;
        const user = { firstname, email, id, token };
        dispatch(loginSuccess({ user }));
        console.log(user);
      },
      (error) => {
        dispatch(loginFailure());
        console.error(error);
      }
    );
};

export const logout = () => async (dispatch) => {
  try {
    return dispatch(logoutSuccess());
    // const res = await axios.post("http://localhost:5000/api/v1/auth/logout");
  } catch (e) {
    return console.error(e.message);
  }
};
