import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { useSelector, useDispatch } from "react-redux";
import { articlesSelector, fetchArticles } from "../../slices/articles";
import { Container, Grid, Button, Typography } from "@material-ui/core";
import Article from "../../components/Article";
import Sidebar from "../../components/Sidebar";
import Modal from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";

const useStyles = makeStyles((theme) => ({
  media: {
    height: 140,
  },
  root: {
    width: "100%",
    maxWidth: 360,
    backgroundColor: "#fff",
  },
  bs: {
    boxShadow: "2px 2px 7px 1px #ccc",
  },
  modal: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: "2px solid #000",
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
  },
}));

const Articles = (props) => {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const dispatch = useDispatch();
  const { articles } = useSelector(articlesSelector);

  var token;
  if (!props.location.state) {
    token = "";
  } else {
    token = props.location.state.id;
  }

  useEffect(() => {
    dispatch(fetchArticles(token));
  }, [dispatch]);

  const renderArticles = () => {
    return articles.map((article) => (
      <Article key={article.id} article={article} excerpt />
    ));
  };

  return (
    <div>
      <Container maxWidth="md">
        <Grid
          alignItems="center"
          container
          spacing={1}
          style={{ marginTop: 40 }}
        >
          <Grid item xs={12} md={8}>
            <Typography variant="h3">Articles</Typography>
          </Grid>
          <Grid item xs={12} md={4}>
            <div style={{ textAlign: "right", paddingTop: 50 }}>
              <Button variant="contained" color="primary" onClick={handleOpen}>
                Create Article
              </Button>
              <Modal
                aria-labelledby="transition-modal-title"
                aria-describedby="transition-modal-description"
                className={classes.modal}
                open={open}
                onClose={handleClose}
                closeAfterTransition
                BackdropComponent={Backdrop}
                BackdropProps={{
                  timeout: 500,
                }}
              >
                <Fade in={open}>
                  <div className={classes.paper}>
                    <h2 id="transition-modal-title">Transition modal</h2>
                    <p id="transition-modal-description">
                      react-transition-group animates me.
                    </p>
                  </div>
                </Fade>
              </Modal>
            </div>
          </Grid>
        </Grid>
        <Grid container spacing={1} style={{ marginTop: 40 }}>
          <Grid item xs={12} md={8}>
            {renderArticles()}
          </Grid>
          <Grid item xs={12} md={4}>
            <div className={(classes.root, classes.bs)}>
              <Sidebar />
            </div>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
};

export default Articles;
