import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { feedsSelector, fetchFeeds } from "../../slices/feeds";
// import { Post } from "../../components/Post";
import Feed from "../../components/Feed";

const Feeds = (props) => {
  const dispatch = useDispatch();
  const { feeds } = useSelector(feedsSelector);

  const token = props.location.state.id;

  // useEffect(() => {
  //   dispatch(fetchFeeds(token));
  // }, [dispatch]);

  useEffect(() => {
    const timer = setTimeout(() => {
      dispatch(fetchFeeds(token));
    }, 5000);
    return () => clearTimeout(timer);
  }, [dispatch]);

  const renderPosts = () => {
    return feeds.map((feed) => <Feed key={feed.id} feed={feed} excerpt />);
  };

  // return <div>{/* {renderPosts()} */}</div>;
};

export default Feeds;
