  //   return (dispatch) => clearTimeout(timer);
  // }, [dispatch]);

  const renderPosts = () => {
    return feeds.map((feed) => <Feed key={feed.id} feed={feed} excerpt />);
  };

  return (
    <div>
      {/* jbbkjn */}
      {renderPosts()}
    </div>
  );
};

export default Feeds;
