import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";
const initialUser = localStorage.getItem("user")
  ? JSON.parse(localStorage.getItem("user"))
  : null;
export const initialState = {
  user: initialUser,
  loading: "",
  hasErrors: "",
};

// A slice of user with our three reducers
const slice = createSlice({
  name: "user",
  initialState,
  reducers: {
    loginSuccess: (state, action) => {
      state.user = action.payload;
      console.log("what" + action.payload);
      localStorage.setItem("user", JSON.stringify(action.payload));
    },
    loginFailure: (state, action) => {
      state.hasErrors = true;
    },
    logoutSuccess: (state, action) => {
      state.user = null;
      localStorage.removeItem("user");
    },
  },
});

// Three actions generated from the slice
export const { loginSuccess, loginFailure, logoutSuccess } = slice.actions;

// A selector
export const userSelector = (state) => state.user;

// The reducer
export default slice.reducer;

// Asynchronous thunk action
export const login = ({ username, password }) => async (dispatch) => {
  axios
    .post("http://localhost:5000/api/v1/auth/signin", {
      email: username,
      password: password,
    })
    .then(
      (response) => {
        const data = response.data.data;
        dispatch(loginSuccess({ response }));
        // console.log(data);
      },
      (error) => {
        dispatch(loginFailure());
        console.error(error);
      }
    );
};

export const logout = () => async (dispatch) => {
  try {
    const res = await axios.post("http://localhost:5000/api/v1/auth/logout");
  } catch (e) {
    return console.error(e.message);
  }
};
