import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";
const initialUser = localStorage.getItem("user")
  ? JSON.parse(localStorage.getItem("user"))
  : null;

export const initialState = {
  user: initialUser,
  feeds: "",
};

// A slice of user with our reducers
const feedsSlice = createSlice({
  name: "feeds",
  initialState,
  reducers: {
    getFeedSuccess: (state, action) => {
      state.user = action.payload;
    },
    getFeedsFailure: (state, action) => {
      state.error = action.payload;
    },
  },
});

// Three actions generated from the slice
export const { getFeedSuccess, getFeedsFailure } = feedsSlice.actions;

// A selector
export const feedsSelector = (state) => state.feeds;

// The reducer
export default feedsSlice.reducer;

// Asynchronous thunk action
export const fetchFeeds = () => async (dispatch) => {
  console.log(initialState.user.user.token);
  axios
    .get("http://localhost:5000/api/v1/feed", {
      //   headers: {
      //     Authorization: initialState.user.user.token,
      //   },
    })
    .then((response) => {
      const feed = response.data.data;
      console.log(feed);
      dispatch(getFeedSuccess({ feed }));
    })
    .catch((error) => {
      const err = error.response;
      console.log(err);
      dispatch(getFeedsFailure({ err }));
    });
};
