import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { useSelector, useDispatch } from "react-redux";
import { feedsSelector, fetchFeeds } from "../../slices/feeds";
import { Container, Grid, Typography } from "@material-ui/core";
import Feed from "../../components/Feed";
import Sidebar from "../../components/Sidebar";

const useStyles = makeStyles((theme) => ({
  media: {
    height: 140,
  },
  root: {
    width: "100%",
    maxWidth: 360,
    backgroundColor: "#fff",
  },
  bs: {
    boxShadow: "2px 2px 7px 1px #ccc",
  },
}));

const Feeds = (props) => {
  const classes = useStyles();
  const dispatch = useDispatch();
  const { feeds } = useSelector(feedsSelector);

  var token;
  if (!props.location.state) {
    token = "";
  } else {
    token = props.location.state.id;
  }

  useEffect(() => {
    dispatch(fetchFeeds(token));
  }, [dispatch]);

  const renderFeeds = () => {
    return feeds.map((feed) => <Feed key={feed.id} feed={feed} excerpt />);
  };

  return (
    <div>
      <Container maxWidth="md">
        <Typography color="textSecondary" component="h1">
          Feeds
        </Typography>
        <Grid container spacing={1} style={{ marginTop: 100 }} justify="center">
          <Grid item xs={12} md={9}>
            {renderFeeds()}
          </Grid>
        </Grid>
      </Container>
    </div>
  );
};

export default Feeds;
