import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { useSelector, useDispatch } from "react-redux";
import { articlesSelector, fetchArticles } from "../../slices/articles";
import { Container, Grid } from "@material-ui/core";
import Article from "../../components/Article";
import Sidebar from "../../components/Sidebar";

const useStyles = makeStyles((theme) => ({
  media: {
    height: 140,
  },
  root: {
    width: "100%",
    maxWidth: 360,
    backgroundColor: "#fff",
  },
  bs: {
    boxShadow: "2px 2px 7px 1px #ccc",
  },
}));

const Articles = (props) => {
  const classes = useStyles();
  const dispatch = useDispatch();
  const { articles } = useSelector(articlesSelector);

  var token;
  if (!props.location.state) {
    token = "";
  } else {
    token = props.location.state.id;
  }

  useEffect(() => {
    dispatch(fetchArticles(token));
  }, [dispatch]);

  const renderArticles = () => {
    return articles.map((article) => (
      <Article key={article.id} article={article} excerpt />
    ));
  };

  return (
    <div>
      <Container maxWidth="md">
        <div style={{ margin: "0 auto" }}>
          <Button>Happy</Button>
        </div>
        <Grid container spacing={1} style={{ marginTop: 100 }}>
          <Grid item xs={12} md={8}>
            {renderArticles()}
          </Grid>
          <Grid item xs={12} md={4}>
            <div className={(classes.root, classes.bs)}>
              <Sidebar />
            </div>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
};

export default Articles;
